import { useState } from "react";
import PropTypes from "prop-types";

TextExpander.propTypes = {
  collapsedNumWords: PropTypes.number,
  expandButtonText: PropTypes.string,
  collapseButtonText: PropTypes.string,
  buttonColor: PropTypes.string,
  expanded: PropTypes.bool,
  className: PropTypes.string,
};

export default function TextExpander({
  collapsedNumWords = 20,
  expandButtonText = "Expand",
  collapseButtonText = "Collapse",
  buttonColor = "red",
  expanded = false,
  className = "",
  children,
}) {
  const [textExpanded, setTextExpanded] = useState(expanded);

  const buttonStyle = {
    backgroundColor: `${buttonColor}`,
  };

  function handleExpanded() {
    console.log("Changing expanded value");
    console.log(textExpanded);
    setTextExpanded(!textExpanded);
    console.log("Changed expanded value");
    console.log(textExpanded);
  }

  return (
    <>
      <span>
        {expanded ? (
          <>
            <p>{children}</p>
            <button style={buttonStyle} onClick={handleExpanded}>
              {collapseButtonText}
            </button>
          </>
        ) : (
          <>
            <p>{children.split(" ").slice(0, collapsedNumWords).join(" ")}</p>
            <button style={buttonStyle} onClick={handleExpanded}>
              {expandButtonText}
            </button>
          </>
        )}
      </span>
    </>
  );
}
